import React from 'react';
import { useParams } from 'react-router-dom';
import { useEffect, useState } from 'react';
import { Axios } from 'axios';
import { useNavigate } from 'react-router-dom';

const Edit = () => {
    let navigate = useNavigate();
    let {contact_id} = useParams();


	const [getFname, setFname] = useState('');
	const [getLname, setLname] = useState('');
	const [getEmail, setEmail] = useState('');
	const [getAddress, setAddress] = useState('');
	const [getSubject, setSubject] = useState('');


    const updateContact = (e)=> {
e.preventDefault()
    }




	return (
		<div className='container1'>
			<div className='cl'>
				<h2>Edit Contact{id}</h2>
			</div>
			<form onSubmit={updateContact}>
				<div className='form-body'>
					<div className='username'>
						<label className='form__label' for='firstName'>
							First Name{' '}
						</label>
						<input
							className='form__input'
							type='text'
							id='firstName'
							placeholder='First Name..'
							value={getFname}
							onChange={(e) => setFname(e.target.value)}
						/>
					</div>
					<div className='lastname'>
						<label className='form__label' for='lastName'>
							Last Name{' '}
						</label>
						<input
							type='text'
							name=''
							id='lastName'
							className='form__input'
							placeholder='LastName'
							value={getLname}
							onChange={(e) => setLname(e.target.value)}
						/>
					</div>
					<div className='email'>
						<label className='form__label' for='email'>
							Email{' '}
						</label>
						<input
							type='email'
							id='email'
							className='form__input'
							placeholder='Email..'
							value={getEmail}
							onChange={(e) => setEmail(e.target.value)}
						/>
					</div>
					<div className='address'>
						<label className='form__label' for='address'>
							Address{' '}
						</label>
						<input
							type='text'
							name=''
							id='address'
							className='form__input'
							placeholder='Address..'
							value={getAddress}
							onChange={(e) => setAddress(e.target.value)}
						/>
					</div>
					<div className='subject'>
						<label className='form__label' for='subject'>
							Subject
						</label>
						<input
							type='text'
							name=''
							id='subject'
							className='form__input'
							placeholder='Write something..'
							value={getSubject}
							onChange={(e) => setSubject(e.target.value)}
						/>
					</div>
				</div>
				<br />
				<div class='footer'>
					<button type='submit' class='btn btn-dark'>
						Submit
					</button>
				</div>
			</form>
		</div>
	);
};
export default Edit;
