import React from 'react';
import { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Link, useNavigate } from 'react-router-dom';
import Axios from 'axios';

const Home = () => {
	let navigate = useNavigate();

	const ButtonEvents = () => {
		alert("Welcome to Burger N' Bite. You're going to another tab.");
	};

	const ButtonEvents2 = (data) => {
		console.log(data + ' Order Now.');
	};

	const [getData, setData] = useState(' ');

	const ButtonEvents3 = (data1) => {
		setData(data1 + ' with Free Shipping');
	};

	const mystyle = {
		textAlignn: 'center',
		marginTop: '1500px',
		marginBottom: '200px',
		padding: ' 10px',
	};

	const style2 = {
		color: 'white',
		fontWeight: 'bold',
		fontFamily: 'sans-serif',
		fontSize: '15px',
		backgroundColor: 'red',
		textAlign: 'center',
		display: 'inline-block',
		padding: '0.5em 3em',
		margin: '0 0.3em 0.3em 0',
		textDecoration: 'none',
		textTransform: 'uppercase',
	};


	const [getUser, setUser] = useState('');
	const [getPass, setPass] = useState('');
	const [getUserlist, setUserlist] = useState([]);


	useEffect(() => {
		Axios.get('http://localhost:3001/get').then((response) => {
			setUserlist(response.data);
		});
	}, []);

	const handleSubmit = (e) => {
		e.preventDefault();
		Axios.post('http://localhost:3001/insert', {
			username: getUser,
			password: getPass,
		}).then((response) => {
			setUserlist([...getUserlist, {	username: getUser,
				password: getPass}])
		});
	};
	const deleteUser =(user)=>{
		Axios.delete([`http://localhost:3001/delete/${user}`]);
			};


	return (
		<div>
			<div className='site-info'>
				<div className='title' onClick={() => ButtonEvents2('So Delicious!')}>
					Burger N' Bite
				</div>
				<div
					className='sub-title'
					onClick={() => ButtonEvents3(' Cash on Delivery')}
				>
					Grilled To Perfection | Dare To Take Another Bite<br></br>
					Real Beef. Real Burgers
					{getData}
					<div>
						<br></br>
						<Link className='nav-link' to='/menu'>
							<button type='button' className='buttonw' onClick={ButtonEvents}>
								Order Now
							</button>
						</Link>

						<br />
						<p>OR</p>
						<br />

						<a href='#sign'>
							<button type='button' className='btn btn-primary' style={style2}>
								REGISTER NOW
							</button>
						</a>
					</div>
				</div>
			</div>
			<div style={mystyle}>
				<a name='sign'>
					<h2>Signup Now</h2>
					<p>Please fill in this form to create an account.</p>
				</a>
				<div className='container p-8 my-2 '>
					<div className='btn-group-vertical'>
						<form onSubmit={handleSubmit}>
							<div>
								<div className='input-group mb-3'>
									<span className='input-group-text'>Username:</span>
									<input
										type='text'
										className='form-control'
										value={getUser}
										onChange={(e) => setUser(e.target.value)}
									/>
								</div>

								<div className='input-group mb-14'>
									<span className='input-group-text'>Password:</span>
									<input
										type='password'
										className='form-control'
										value={getPass}
										onChange={(e) => setPass(e.target.value)}
									/>
								</div>

								<div className='col-58'>
									<button
										id='Popover1'
										type='submit'
										className='button1'
										onfocus="this.value=''"
										data-bs-toggle='popover'
										data-bs-content='Some content inside the popover'
									>
										Submit
									</button>
								</div>
							</div>
						</form>
					</div>
				</div>
				<br />
				<button
					type='button'
					className='btn btn-warning'
					onClick={() => {
						navigate('/menu');
					}}
				>
					Go to Menu
				</button>
				<a href='#list'>
					<button type='button' className='btn btn-danger'>
						 Signup List
					</button>
				</a>
				<br /> <br /> <br /> <br /> <br />


				<div className='container2 p-5'>
			<a name='list'>
			<h2>Signup Information</h2>
				</a>
				<table className='table table-dark table-striped'>
					<thead>
						<tr>
							<th>Username</th>
							<th>Password</th>
							<th>Action</th>
						</tr>
					</thead>
					<tbody>
						{getUserlist.map((value) => {
							return (
								<tr key={value.id}>
									<td>{value.username}</td>
									<td>{value.password}</td>

									<td>
										<div className='btn-group'>
											<button type='button' className='btn btn-warning'>
												Edit
											</button>
											<button type='button' className='btn btn-danger' onClick={()=> {deleteUser(value.username)}}>
												Delete
											</button>
										</div>
									</td>
								</tr>
							);
						})}
					</tbody>
				</table>
</div>
			</div>
		</div>
	);
};

export default Home;
