import React from 'react';

  class Menu extends React.Component {
    constructor(props) {
      super(props);
      this.state = {value: 'Burger'};
  
      this.handleChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
    }
  
    handleChange(event) {
      this.setState({value: event.target.value});
    }
  
    handleSubmit(event) {
      alert('Your Order is: ' + this.state.value);
      event.preventDefault();
    }
  
    render() {
      return (
        <div> 

        <table class="table">
          <thead>
            <tr>
              <th>Burger Name</th>
              <th></th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td scope="row"></td>
              <td>  <img 
      src="https://purlp.com/wp-content/uploads/2021/02/Burger-King-Menu-Whopper.png"
      alt="new" width={'100px'}
      /></td>
              <td>  <img 
      src="https://purlp.com/wp-content/uploads/2021/02/Burger-King-Menu-Whopper.png"
      alt="new"
      /></td>
            </tr>

          </tbody>
        </table>
      
        <form onSubmit={this.handleSubmit}>
          <label>
            Pick your Order:
            <select value={this.state.value} onChange={this.handleChange}>
              <option value="burger king whopper jr.">Burger King Whopper Jr.</option>
              <option value="whiskey river bbq burger.">Whiskey River BBQ Burger</option>
              <option value="bacon cheeseburger">Bacon Cheeseburger</option>
              <option value="shake shack shackburger.">Shake Shack ShackBurger</option>
            </select>
          </label>
          <input type="submit" value="Add to Cart" />
        </form>
        </div> 
      );
    }
};
export default Menu;
