import React from 'react';
import { useParams } from 'react-router-dom';
import { useEffect, useState } from 'react';
import { Axios } from 'axios';
import { useNavigate } from 'react-router-dom';

const Edit = () => {
	let navigate = useNavigate();
	let { id } = useParams();

	const [getUpdFname, setUpdFname] = useState('');
	const [getUpdLname, setUpdLname] = useState('');
	const [getUpdEmail, setUpdEmail] = useState('');
	const [getUpdAddress, setUpdAddress] = useState('');
	const [getUpdSubject, setUpdSubject] = useState('');

	const [getContactlist, setContactlist] = useState([]);


	const wage = req.body.wage;


	const updateContact = (id) => {
		Axios.put('http://localhost:3001/updateContact', {
			id: id,
			fname : getUpdFname,
			lname : getUpdLname,
			email : getUpdEmail,
			address : getUpdAddress,
			subject : getUpdSubject,
		}).then((response) => {
			setContactlist(
				getContactlist.map((value) => {
					return value.id === id
						? {
								fname: getUpdFname,
								lname: value.lname,
								email: getUpdEmail,
								address: getUpdAddress,
								subject: getUpdSubject,
						  }
						: value;
				})
			);
		});
	};





	return (
		<div>
			<div>
				<br /> <br /> <br />
				<div className='body1'>
					This is our Contact Information <br />
					Please reach us @www.burgernbite.com <br />
					Contact us with: 000123456
				</div>
				<br />
				<br />
				<div className='container1'>
					<div className='cl'>
						<hr />
						<h2>Update Contact Form {id}</h2>
					</div>

					{getContactlist.map((value, key) => {
						return(
							<form onSubmit={updateContact}>
							<div className='form-body'>
								<div className='username'>
									<label className='form__label' htmlFor='firstName'>
										First Name{' '}
									</label>
									<input
										className='form__input'
										type='text'
										id='firstName'
										placeholder='First Name..'
										onChange={(e) => { setUpdFname(e.target.value);

										}}
									/>
								</div>
								<div className='lastname'>
									<label className='form__label' htmlFor='lastName'>
										Last Name{' '}
									</label>
									<input
										type='text'
										name=''
										id='lastName'
										className='form__input'
										placeholder='LastName'
										onChange={(e) => { setUpdLname(e.target.value);

										}}
									/>
								</div>
								<div className='email'>
									<label className='form__label' htmlFor='email'>
										Email{' '}
									</label>
									<input
										type='email'
										id='email'
										className='form__input'
										placeholder='Email..'
										onChange={(e) => { setUpdEmail(e.target.value);

										}}
									/>
								</div>
								<div className='address'>
									<label className='form__label' htmlFor='address'>
										Address{' '}
									</label>
									<input
										type='text'
										name=''
										id='address'
										className='form__input'
										placeholder='Address..'
										onChange={(e) => { setUpdAddress(e.target.value);

										}}
									/>
								</div>
								<div className='subject'>
									<label className='form__label' htmlFor='subject'>
										Subject
									</label>
									<input
										type='text'
										name=''
										id='subject'
										className='form__input'
										placeholder='Write something..'
										onChange={(e) => { setUpdSubject(e.target.value);

										}}
									/>
								</div>
							</div>
							<br />

							<button
					  onClick={() => {
						updateContact (value.id);
					  }}
					>

					  Update
					</button>


						</form>

						);

					})}

				<br />
				<button
					type='button'
					className='bt1'
					onClick={() => {
						navigate('/contact');
					}}
				>
					Back to Contact
				</button>
			</div>
		</div>
		</div>
	);
};
export default Edit;
