import React from 'react';

  class Menu extends React.Component {
    constructor(props) {
      super(props);
      this.state = {value: 'Burger'};
  
      this.handleChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
    }
  
    handleChange(event) {
      this.setState({value: event.target.value});
    }
  
    handleSubmit(event) {
      alert('Your favorite flavor is: ' + this.state.value);
      event.preventDefault();
    }
  
    render() {
      return (
        <form onSubmit={this.handleSubmit}>
          <label>
            Pick your Order:
            <select value={this.state.value} onChange={this.handleChange}>
              <option value="burger king whopper jr.">Burger King Whopper Jr.</option>
              <option value="red Robin Whiskey River BBQ Burger.">Red Robin Whiskey River BBQ Burger</option>
              <option value="Red Robin Bacon Cheeseburger">Red Robin Bacon Cheeseburger</option>
              <option value="Shake Shack ShackBurger.">Shake Shack ShackBurger</option>
            </select>Mango
          </label>
          <input type="submit" value="Add to Cart" />
        </form>
      );
    }
};
export default Menu;
