import React from 'react';

class Menu extends React.Component {
	constructor(props) {
		super(props);
		this.state = { value: 'Burger' };

		this.handleChange = this.handleChange.bind(this);
		this.handleSubmit = this.handleSubmit.bind(this);
	}

	handleChange(event) {
		this.setState({ value: event.target.value });
	}

	handleSubmit(event) {
		alert('Your Order is: ' + this.state.value);
		event.preventDefault();
	}

	render() {
		return (
			<div>
				<table class='table'>
					<thead>
						<tr>
							<th>Burger King Menu Whopper</th>
							<th>hgh</th>
							<th>burger</th>
						</tr>
					</thead>
					<tbody>
						<tr>
							<td scope='row'></td>
							<td>
								<img
									src='https://www.independent.co.uk/life-style/food-and-drink/burger-king-free-whopper-burger-app-b2048219.html'
									alt='new'
									width={'500px'}
									height={'500px'}
								/>jhgjhgj
							</td>
							<td>
					
								<img
									src='https://pbs.twimg.com/media/BzdQxfTCcAEdQkV.png:large'
									alt='new'
									width={'500px'}
									height={'500px'}
								/>
							</td>

							<td>
								<img
									src='https://media.istockphoto.com/photos/bacon-burger-picture-id520215281?k=20&m=520215281&s=612x612&w=0&h=kD__ABaZrMo2DqW8huIMfuJ-GCW25pGK6KzgJ9bj394='
									alt='new'
									width={'500px'}
									height={'500px'}
								/>
							</td>

							<td>
								{' '}
								<img
									src='https://food.fnr.sndimg.com/content/dam/images/food/fullset/2017/5/10/0/FNM_060117-Shake-Shack-Style-Burgers-Recipe_s4x3.jpg.rend.hgtvcom.616.462.suffix/1494459385949.jpeg'
									alt='new'
									width={'500px'}
									height={'500px'}
								/>
							</td>
						</tr>
					</tbody>
				</table>

				<form onSubmit={this.handleSubmit}>
					<label>
						Pick your Order:
						<select value={this.state.value} onChange={this.handleChange}>
							<option value='burger king whopper jr.'>
								Burger King Whopper Jr.
							</option>
							<option value='whiskey river bbq burger.'>
								Whiskey River BBQ Burger
							</option>
							<option value='bacon cheeseburger'>Bacon Cheeseburger</option>
							<option value='shake shack shackburger.'>
								Shake Shack ShackBurger
							</option>
						</select>
					</label>
					<input type='submit' value='Add to Cart' />
				</form>
			</div>
		);
	}
}
export default Menu;
