import React from 'react';

const style = {
  fontSize: "50px", 
  fontFamily: "serif",
  backgroundColor:'#F8F8FF',
}


const style1 = {
  color: 'black', 
  fontSize: "30px", 
  fontWeight: 'bold',
  backgroundColor:'#F8F8FF',
  marginRight: '250px',
}


const style2 = {
  color: 'black', 
  fontSize: "30px",
  backgroundColor: 'red',
  textAlign: 'center',
}

class Menu extends React.Component {
	constructor(props) {
		super(props);
		this.state = { value: 'Burger' };

		this.handleChange = this.handleChange.bind(this);
		this.handleSubmit = this.handleSubmit.bind(this);
	}

	handleChange(event) {
		this.setState({ value: event.target.value });
	}

	handleSubmit(event) {
		alert('Your Order is: ' + this.state.value);
		event.preventDefault();
	}

	render() {
		return (
			<div style={style}>
				<table class='table'>
        <br />
        <br />
					<thead>
            
						<tr >
							<th scope='row'>Burger King Menu Whopper</th>
							<th scope='row'>Whiskey River BBQ Burger</th>
							<th scope='row'>Bacon Cheeseburger</th>
              <th scope='row'>Shake Shack Burger</th>
						</tr>
					</thead>
					<tbody>
						<tr>
            <td >
              <img
									src='https://www.thepackagingcompany.us/knowledge-sharing/wp-content/uploads/sites/2/2019/05/ip-bk-whopper-blog.jpg'
									alt='new'
									width={'400px'}
									height={'500px'}
								/>
              </td>
					
							
							<td>
					
								<img
									src='https://media.istockphoto.com/photos/fried-chicken-sandwich-picture-id511520568?k=20&m=511520568&s=612x612&w=0&h=4SChcngzqOE2nNCEQV26ZCtA84BlhAshnxcdN5Beb7I='
									alt='new'
									width={'400px'}
									height={'500px'}
								/>
							</td>

							<td>
								<img
									src='https://previews.123rf.com/images/belchonock/belchonock1902/belchonock190209458/116958943-tasty-burger-with-bacon-on-white-background.jpg'
									alt='new'
									width={'400px'}
									height={'500px'}
								/>
							</td>

							<td>
								<img
									src='https://food.fnr.sndimg.com/content/dam/images/food/fullset/2017/5/10/0/FNM_060117-Shake-Shack-Style-Burgers-Recipe_s4x3.jpg.rend.hgtvcom.616.462.suffix/1494459385949.jpeg'
									alt='new'
									width={'400px'}
									height={'500px'}
								/>
							</td>
						</tr>
					</tbody>
				</table>
<br />

				<form onSubmit={this.handleSubmit}>
					<label style={style1}>
						Pick your Order:
						<select value={this.state.value} onChange={this.handleChange}>
							<option value='burger king whopper jr.'>
								Burger King Whopper Jr.
							</option>
							<option value='whiskey river bbq burger.'>
								Whiskey River BBQ Burger
							</option>
							<option value='bacon cheeseburger'>Bacon Cheeseburger</option>
							<option value='shake shack shackburger.'>
								Shake Shack ShackBurger
							</option>
						</select>
					</label><br />
					<input type='submit' value='Add to Cart' style={style2}/>
				</form>
        <br />
<br />
<br />
<br />
			</div>
		);
	}
}
export default Menu;
