import React from 'react';
import {useNavigate} from 'react-router-dom'

const mystyle = {
	color: 'white',
	fontSize: '30px',
	fontFamily: 'san-serif',
	textAlign: 'justify',
	padding: '100px',
	textShadow: '2px 2px black',
	marginTop: 'px',
	
};

const About = () => {
	return (
		<div>
<about style={mystyle}>

			  <div>
				  <div className='about' style={mystyle}>

  
					  
					  Founded in 2022, in Cabadbaran, the first Burger N' Bite opened with
					  one single notion in mind; to serve a better burger and, in the
					  process, helped create a new category. The every bite a better burger
					  experience is known for their namesake smashing technique – smashing a
					  fresh, never frozen, 100% Certified Angus Beef hand-packed ball of
					  meat on a hot seasoned grill – delivering an elevated burger
					  experience, where every bite is made to savor.

					  <button type='button' className='btn btn-primary'>Back to Homepage</button>
  
				  </div>
			  </div>
		  </about>
			</div>
		
	);
};
export default About;
