import React from 'react';
import { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Popover, PopoverHeader, PopoverBody } from 'reactstrap';
import { Link } from 'react-router-dom';

const mystyle = {
	marginTop: '400px',
};

const Home = () => {
	const ButtonEvents = () => {
		alert("Welcome to Burger N' Bite. You're going to another tab.");
	};

	const ButtonEvents2 = (data) => {
		console.log(data + ' Order Now.');
	};

	const [getData, setData] = useState(' ');

	const ButtonEvents3 = (data1) => {
		setData(data1 + ' with Free Shipping');
	};

	const [getUser, setUser] = useState('');
	const [getPass, setPass] = useState('');

	const handleSubmit = (e) => {
		e.preventDefault();
	};

	const [popoverOpen, setPopoverOpen] = React.useState(false);

	return (
		<div>
			<div className='site-info'>
				<div className='title' onClick={() => ButtonEvents2('So Delicious!')}>
					Burger N' Bite
				</div>
				<div
					className='sub-title'
					onClick={() => ButtonEvents3('Cash on Delivery')}
				>
					Grilled To Perfection | Dare To Take Another Bite<br></br>
					{getData}
					<div>
						<br></br>
						<Link className='nav-link' to='/menu'>
							<button type='button' className='buttonw' onClick={ButtonEvents}>
								Order Now{' '}
							</button>{' '}
						</Link>

						<br />
						<p>OR</p>
						<br />
					</div>
				</div>
			</div>

			<h3>Signup Now</h3>
			<div className='container p-8 my-2 '>
				<div className='btn-group-vertical'>
					<form onSubmit={handleSubmit}>
						<div style={mystyle}>
							<div className='input-group mb-3'>
								<span className='input-group-text'>Username:</span>
								<input
									type='text'
									className='form-control'
									value={getUser}
									onChange={(e) => setUser(e.target.value)}
								/>
							</div>

							<div className='input-group mb-14'>
								<span className='input-group-text'>Password:</span>
								<input
									type='text'
									className='form-control'
									value={getPass}
									onChange={(e) => setPass(e.target.value)}
								/>
							</div>

							<div className='col-58'>
								<button
									id='Popover1'
									type='submit'
									className='button1'
									data-bs-toggle='popover'
									data-bs-content='Some content inside the popover'
								>
									Submit
								</button>
							</div>
							<p>You're Username:{getUser}</p>
							<p>You're Password:{getPass}</p>

							<div className='pop-over'>
								<Popover
									placement='right'
									isOpen={popoverOpen}
									target='Popover1'
									toggle={() => {
										setPopoverOpen(!popoverOpen);
									}}
								>
									<PopoverHeader>What would you like?</PopoverHeader>
									<PopoverBody>We Deliver in front on your door.</PopoverBody>
								</Popover>
							</div>
						</div>
					</form>
				</div>
			</div>
		</div>
	);
};

export default Home;
