import React from 'react';
import { useState, useEffect } from 'react';
import Axios from 'axios';

const style = {
	fontSize: '30px',
	fontFamily: 'serif',
	backgroundColor: '#F8F8FF',
	textTransform: 'uppercase',
	textAlign: 'center',
};

const Menu = () => {
	const [getOrderburger1, setOrderburger1] = useState('Burger King');
	const [getOrderburger2, setOrderburger2] = useState('WHISKEY RIVER ');

	const [getOrderlist, setOrderlist] = useState('');

	useEffect(() => {
		Axios.get('http://localhost:3001/getBurger').then((response) => {
			setOrderlist(response.data);
		});
	}, []);

	const handleSubmit = (e) => {
		e.preventDefault();
		Axios.post('http://localhost:3001/insertBurger', {
			burger_name: getOrderburger1,
			burger_name1: getOrderburger2,
		}).then((response) => {
			alert('Insert Successfully');
		});
	};

	const handleSubmit1 = (e) => {
		e.preventDefault();
		Axios.post('http://localhost:3001/insertBurger1', {
			burger_name1: getOrderburger2,
		}).then((response) => {
			alert('Insert Successfully');
		});
	};

	return (
		<div style={style}>
			<table class='table'>
				<tbody>
					<tr className='cl3'>
						<th scope='row'>Burger King Menu Whopper</th>
						<th scope='row'>Whiskey River BBQ Burger</th>
						<th scope='row'>Bacon Cheeseburger</th>
						<th scope='row'>Shake Shack Burger</th>
					</tr>
					<tr>
						<td>
							<div class='card'>
								<form onSubmit={handleSubmit}>
									<img
										src='https://www.thepackagingcompany.us/knowledge-sharing/wp-content/uploads/sites/2/2019/05/ip-bk-whopper-blog.jpg'
										alt='new'
										width={'300px'}
										height={'300px'}
									/>
									<h2>
										<input
											type='button'
											className='btw'
											value={getOrderburger1}
											onChange={(e) => setOrderburger1(e.target.value)}
										/>
									</h2>
									<p className='btw'>₱50</p>

									<div className='card'>
										Some text about the jeans. Super slim and comfy lorem ipsum
										lorem jeansum. Lorem jeamsun denim lorem jeansum.
										<button type='submit'>Add to Cart</button>
									</div>
								</form>
							</div>
						</td>

						<td>
							<div class='card'>
								<form onSubmit={handleSubmit1}>
									<img
										src='https://media.istockphoto.com/photos/fried-chicken-sandwich-picture-id511520568?k=20&m=511520568&s=612x612&w=0&h=4SChcngzqOE2nNCEQV26ZCtA84BlhAshnxcdN5Beb7I='
										alt='new'
										width={'300px'}
										height={'300px'}
									/>
									<h2>
										<input
											type='button'
											className='btw'
											value={getOrderburger2}
											onChange={(e) => setOrderburger2(e.target.value)}
										/>
									</h2>
									<p className='btw'>₱45</p>

									<div className='card'>
										Some text about the jeans. Super slim and comfy lorem ipsum
										lorem jeansum. Lorem jeamsun denim lorem jeansum.
										<button type='submit'>Add to Cart</button>
									</div>
								</form>
							</div>
						</td>

						<td>
							<div class='card'>
								<form onSubmit={handleSubmit1}>
									<img
										src='https://previews.123rf.com/images/belchonock/belchonock1902/belchonock190209458/116958943-tasty-burger-with-bacon-on-white-background.jpg'
										alt='new'
										width={'300px'}
										height={'300px'}
									/>
									<h2>
										<input
											type='button'
											className='btw'
											value={getOrderburger2}
											onChange={(e) => setOrderburger2(e.target.value)}
										/>
									</h2>
									<p className='btw'>₱45</p>

									<div className='card'>
										Some text about the jeans. Super slim and comfy lorem ipsum
										lorem jeansum. Lorem jeamsun denim lorem jeansum.
										<button type='submit'>Add to Cart</button>
									</div>
								</form>
							</div>
						</td>

						<td>
							<div class='card'>
								<form onSubmit={handleSubmit1}>
									<img
										src='https://food.fnr.sndimg.com/content/dam/images/food/fullset/2017/5/10/0/FNM_060117-Shake-Shack-Style-Burgers-Recipe_s4x3.jpg.rend.hgtvcom.616.462.suffix/1494459385949.jpeg'
										alt='new'
										width={'300px'}
										height={'300px'}
									/>
									<h2>
										<input
											type='button'
											className='btw'
											value={getOrderburger2}
											onChange={(e) => setOrderburger2(e.target.value)}
										/>
									</h2>
									<p className='btw'>₱45</p>

									<div className='card'>
										Some text about the jeans. Super slim and comfy lorem ipsum
										lorem jeansum. Lorem jeamsun denim lorem jeansum.
										<button type='submit'>Add to Cart</button>
									</div>
								</form>
							</div>
						</td>
					</tr>
				</tbody>
			</table>
			<br /> <br /> <br /> <br /> <br /> <br /> <br />
		</div>
	);
};
export default Menu;
