import React from 'react';
import logo from 'my-app/src/images/ham.jpg';
  class Menu extends React.Component {
    constructor(props) {
      super(props);
      this.state = {value: 'Burger'};
  
      this.handleChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
    }
  
    handleChange(event) {
      this.setState({value: event.target.value});
    }
  
    handleSubmit(event) {
      alert('Your Order is: ' + this.state.value);
      event.preventDefault();
    }
  
    render() {
      return (
        <div> 
        <img src={'my-app/src/images/ham.jpg'} alt='' /> 
        <form onSubmit={this.handleSubmit}>
          <label>
            Pick your Order:
            <select value={this.state.value} onChange={this.handleChange}>
              <option value="burger king whopper jr.">Burger King Whopper Jr.</option>
              <option value="whiskey river bbq burger.">Whiskey River BBQ Burger</option>
              <option value="bacon cheeseburger">Bacon Cheeseburger</option>
              <option value="shake shack shackburger.">Shake Shack ShackBurger</option>
            </select>
          </label>
          <input type="submit" value="Add to Cart" />
        </form>
        </div> 
      );
    }
};
export default Menu;
